// src/pages/HistoriaMedica.js
import React, { useState } from "react";
import axios from "axios";

const HistoriaMedica = () => {
  const [formData, setFormData] = useState({
    PacienteID: "",
    FechaConsulta: new Date().toISOString().split("T")[0],
    MotivoConsulta: "",
    Diagnostico: "",
    Tratamiento: "",
    AntecedentesFamiliares: "",
    EnfermedadesOcularesPrevias: "",
    PrescripcionLentes: "",
    UsoLentesContacto: "",
    CirugiasOcularesPrevias: "",
    ExamenVisual: "",
    PresionOcular: "",
    CampoVisual: "",
    FondoDeOjo: "",
    TratamientosPrevios: "",
    MedicamentosRecetados: "",
    RecomendacionesMedicas: "",
    ProximoControl: "",
    TomografiaRetina: "",
    EcografiaOcular: "",
    OjoDerechoEstado: "",
    OjoIzquierdoEstado: "",
    NivelDolor: "",
    Observaciones: "",
  });

  const [historiaMedica, setHistoriaMedica] = useState([]);
  const [paciente, setPaciente] = useState(null);
  const [buscarPacienteID, setBuscarPacienteID] = useState("");
  const [error, setError] = useState("");

  // Ajusta la altura del textarea dinámicamente
  const handleAutoResize = (e) => {
    e.target.style.height = "auto";
    e.target.style.height = `${e.target.scrollHeight}px`;
  };

  // Cargar la historia médica del paciente según su documento
  const handleVerHistoria = () => {
    if (buscarPacienteID) {
      axios
        .get(`http://localhost:5000/api/historia-medica/${buscarPacienteID}`)
        .then((response) => {
          if (response.data.length > 0) {
            setHistoriaMedica(response.data);
            // Asumiendo que la respuesta incluye un objeto "Paciente" y un campo "PacienteID"
            setPaciente(response.data[0]?.Paciente);
            setFormData((prev) => ({
              ...prev,
              PacienteID: response.data[0]?.PacienteID,
            }));
            setError("");
          } else {
            setError("Paciente no registrado - debe registrar los datos del paciente antes de poder crear historia médica.");
            setPaciente(null);
            setFormData((prev) => ({
              ...prev,
              PacienteID: "No hay consultas previas para este paciente, proceda a registrar primera consulta",
            }));
          }
        })
        .catch((err) => {
          console.error("Error al obtener la historia médica:", err);
          setError("Hubo un error al obtener los datos.");
        });
    }
  };

  // Manejo de cambios en el formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleBuscarPaciente = (e) => {
    setBuscarPacienteID(e.target.value);
  };

  // Envío del formulario para registrar una nueva consulta en la historia médica
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.PacienteID || !formData.FechaConsulta || !formData.MotivoConsulta) {
      setError("Por favor, complete los campos obligatorios.");
      return;
    }

    axios
      .post("http://localhost:5000/api/historia-medica", formData)
      .then((response) => {
        alert("Historia médica registrada con éxito.");
        // Reiniciar el formulario dejando la fecha actual por defecto
        setFormData({
          PacienteID: "",
          FechaConsulta: new Date().toISOString().split("T")[0],
          MotivoConsulta: "",
          Diagnostico: "",
          Tratamiento: "",
          AntecedentesFamiliares: "",
          EnfermedadesOcularesPrevias: "",
          PrescripcionLentes: "",
          UsoLentesContacto: "",
          CirugiasOcularesPrevias: "",
          ExamenVisual: "",
          PresionOcular: "",
          CampoVisual: "",
          FondoDeOjo: "",
          TratamientosPrevios: "",
          MedicamentosRecetados: "",
          RecomendacionesMedicas: "",
          ProximoControl: "",
          TomografiaRetina: "",
          EcografiaOcular: "",
          OjoDerechoEstado: "",
          OjoIzquierdoEstado: "",
          NivelDolor: "",
          Observaciones: "",
        });
        setError("");
      })
      .catch((err) => {
        console.error("Error al registrar la historia médica:", err);
        setError("Hubo un error al registrar la consulta.");
      });
  };

  return (
    <div>
      <h1>Historia Médica</h1>

      <label>Buscar por Número de Cédula o Pasaporte:</label>
      <input
        type="text"
        value={buscarPacienteID}
        onChange={handleBuscarPaciente}
        placeholder="Ingrese el número de documento"
      />
      <button onClick={handleVerHistoria}>Buscar</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {paciente && (
        <div>
          <h3>Información del Paciente:</h3>
          <p>
            <strong>Nombre:</strong> {paciente.nombre} {paciente.primerApellido}
          </p>
          <p>
            <strong>Sexo:</strong> {paciente.sexo}
          </p>
          <p>
            <strong>Fecha de Nacimiento:</strong> {paciente.fechaNacimiento}
          </p>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <label>Paciente:</label>
        <input type="text" name="PacienteID" value={formData.PacienteID} disabled />

        <label>Fecha de Consulta:</label>
        <input
          type="date"
          name="FechaConsulta"
          value={formData.FechaConsulta}
          onChange={handleChange}
          required
        />

        <label>Motivo de la Consulta:</label>
        <input
          type="text"
          name="MotivoConsulta"
          value={formData.MotivoConsulta}
          onChange={handleChange}
          required
        />

        <label>Diagnóstico:</label>
        <input type="text" name="Diagnostico" value={formData.Diagnostico} onChange={handleChange} />

        <label>Tratamiento:</label>
        <input type="text" name="Tratamiento" value={formData.Tratamiento} onChange={handleChange} />

        <label>Antecedentes Familiares:</label>
        <input type="text" name="AntecedentesFamiliares" value={formData.AntecedentesFamiliares} onChange={handleChange} />

        <label>Enfermedades Oculares Previas:</label>
        <input type="text" name="EnfermedadesOcularesPrevias" value={formData.EnfermedadesOcularesPrevias} onChange={handleChange} />

        <label>Prescripción de Lentes:</label>
        <input type="text" name="PrescripcionLentes" value={formData.PrescripcionLentes} onChange={handleChange} />

        <label>Uso de Lentes de Contacto:</label>
        <input type="text" name="UsoLentesContacto" value={formData.UsoLentesContacto} onChange={handleChange} />

        <label>Cirugías Oculares Previas:</label>
        <input type="text" name="CirugiasOcularesPrevias" value={formData.CirugiasOcularesPrevias} onChange={handleChange} />

        {/* Puedes agregar los demás campos de manera similar */}

        <label>Observaciones:</label>
        <textarea
          name="Observaciones"
          value={formData.Observaciones}
          onChange={handleChange}
          onInput={handleAutoResize}
          style={{ resize: "none", width: "100%", overflow: "hidden" }}
        />

        <button type="submit">Registrar Consulta</button>
      </form>

      <hr />

      <h2>Historial Médico</h2>
      <ul>
        {historiaMedica.length > 0 ? (
          historiaMedica.map((consulta, index) => (
            <li key={index}>
              <strong>Fecha:</strong> {consulta.FechaConsulta} <br />
              <strong>Motivo de Consulta:</strong> {consulta.MotivoConsulta} <br />
              <strong>Diagnóstico:</strong> {consulta.Diagnostico} <br />
              <strong>Tratamiento:</strong> {consulta.Tratamiento} <br />
              <strong>Observaciones:</strong> {consulta.Observaciones}
            </li>
          ))
        ) : (
          <p>No hay consultas previas para este paciente.</p>
        )}
      </ul>
    </div>
  );
};

export default HistoriaMedica;
