// src/pages/MediosDePago.js
import React from 'react';

const MediosDePago = () => {
  // Tasa BCV oficial (valor de ejemplo, actualiza según la tasa real)
  const rateBCV = 25000; // Ejemplo: 1 USD = 25,000 Bs

  // Opciones de pago con precios en bolívares
  const paymentOptions = [
    { method: 'Pago con tarjeta de crédito', priceBs: 500000 },
    { method: 'Transferencia bancaria', priceBs: 450000 },
    { method: 'Pago en efectivo', priceBs: 480000 },
    // Puedes agregar más métodos y precios aquí
  ];

  // Calcula el precio equivalente en USD para cada opción
  const optionsWithUSD = paymentOptions.map(option => ({
    ...option,
    priceUSD: (option.priceBs / rateBCV).toFixed(2) // redondeo a 2 decimales
  }));

  return (
    <div>
      <h1>Medios de Pago</h1>
      <p>
        Los precios se muestran en bolívares (Bs) según la tasa oficial BCV y su equivalente en USD.
      </p>
      <table style={{ borderCollapse: 'collapse', width: '100%' }} border="1" cellPadding="10">
        <thead>
          <tr>
            <th>Método de Pago</th>
            <th>Precio en Bs</th>
            <th>Precio en USD</th>
          </tr>
        </thead>
        <tbody>
          {optionsWithUSD.map((option, index) => (
            <tr key={index}>
              <td>{option.method}</td>
              <td>{option.priceBs.toLocaleString()}</td>
              <td>${option.priceUSD}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <p>Tasa BCV oficial: 1 USD = {rateBCV.toLocaleString()} Bs</p>
    </div>
  );
};

export default MediosDePago;
